import mongoose from "mongoose";

const taskSchema = new mongoose.Schema({
    title: {
        type: String,
        required: true,
    },
    frontEndCode: {
        type: String,
        required: true,
    },
    backEndCode: {
        type: String,
        required: true,
    },
    frontEndUrl: {
        type: String,
        required: true,
    },
    backEndUrl: {
        type: String,
        required: true,
    },
    comment:String,
    score:  Number,
    

    
    status: String,
    taskId: String,
    user: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'user',
    },
    createdAt: {
        type: Date,
        default: Date.now,
    },
    updatedAt: {
        type: Date,
        default: Date.now,

    }

})
 

export default mongoose.model("task", taskSchema, "tasks");
