import mongoose from "mongoose";

const taskSchema = new mongoose.Schema({
    title: {
        type: String,
        required: true,
    },
    frontEndCode: {
        type: String,
       
    },
    backEndCode: {
        type: String,
     
    },
    frontEndUrl: {
        type: String,
      
    },
    backEndUrl: {
        type: String,
     
    },
    typeoftask: String,
    comment:String,
    score:  Number,
    

    
    status: String,
    taskId: String,
    user: String,
    createdAt: String,
    updateAt: String

})
 

export default mongoose.model("task", taskSchema, "tasks");
