import mongoose from "mongoose";

const count = 0;
const userSchema = new mongoose.Schema({
    username: String,
    name: String,
    passwordHash: String,

    token: String,
    task : [{type:Object}],
    role: String,
    //
    activationStatus: {
        type: String,
        // enum:[type],
        default: "Inactive"
    }
});
export default mongoose.model('user', userSchema, 'users');



